import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Selection(){
  const [selection, setSelection] = useState([]);
  const navigate = useNavigate();

  useEffect(()=>{ setSelection(JSON.parse(localStorage.getItem('selection') || '[]')); }, []);

  function removeIndex(i){
    const s = [...selection]; s.splice(i,1); setSelection(s); localStorage.setItem('selection', JSON.stringify(s));
  }

  function checkout(){
    navigate('/payment');
  }

  const total = selection.reduce((sum,it)=>sum + Number(it.price || 0), 0);

  return (
    <div className='screen container'>
      <h2>Your Selection</h2>
      {selection.length===0 ? <p>No items selected.</p> : (
        <div>
          <ul className='selection-list'>
            {selection.map((it,idx)=>(
              <li key={idx}>
                <span>{it.name} - Rs {it.price}</span>
                <button className='btn danger' onClick={()=>removeIndex(idx)}>Remove</button>
              </li>
            ))}
          </ul>
          <div className='checkout'>
            <div>Total: Rs {total}</div>
            <button className='btn primary' onClick={checkout}>Checkout</button>
          </div>
        </div>
      )}
    </div>
  );
}
