// apiConfig.js - frontend uses this to call backend
const BASE = process.env.REACT_APP_API_BASE || 'http://localhost:5000';

export default function api(path, opts={}){
  const url = BASE + path;
  const final = { headers: { 'Content-Type': 'application/json' }, ...opts };
  return fetch(url, final);
}
