import React, { useEffect, useState } from 'react';
import api from '../apiConfig';

export default function Menu(){
  const [menu, setMenu] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(()=>{ fetchMenu(); }, []);

  async function fetchMenu(){
    setLoading(true);
    try{
      const res = await api('/menu');
      setMenu(await res.json());
    }catch(e){ alert('Failed to fetch menu: ' + e.message); }
    setLoading(false);
  }

  function addToSelection(item){
    const sel = JSON.parse(localStorage.getItem('selection') || '[]');
    sel.push(item);
    localStorage.setItem('selection', JSON.stringify(sel));
    alert(item.name + ' added to selection');
  }

  return (
    <div className='screen container'>
      <h2>Menu</h2>
      {loading ? <p>Loading...</p> : (
        <div className='grid'>
          {menu.map(it=>(
            <div key={it.id} className='card'>
              <img src={it.image} alt={it.name} className='thumb' />
              <div className='card-body'>
                <div className='card-title'>{it.name}</div>
                <div className='card-desc'>{it.description}</div>
              </div>
              <div className='card-right'>
                <div className='price'>Rs {it.price}</div>
                <button className='btn primary' onClick={()=>addToSelection(it)}>Add</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
